import React, { useContext, useRef, useState } from 'react'
import { UserContext } from '../Store'
import { useNavigate } from 'react-router-dom'

export default function Payment() {
    const store = useContext(UserContext)
    const [form, setForm] = useState({ 
        cardHolderName: "", 
        cardNumber: "", 
        Expiry_Date_MM: "", 
        Expiry_Date_YY: "", 
        CVV: "", 
        total: store.cartDetail.total 
    })

    const [selectedMethod, setSelectedMethod] = useState("COD") // ✅ New
    const cardHolderName = useRef()
    const cardNumber = useRef()
    const Expiry_Date_MM = useRef()
    const Expiry_Date_YY = useRef()
    const CVV = useRef()
    const navigate = useNavigate();

    let handleClick = (e) => {
        e.preventDefault();
        let user = localStorage.getItem("user")
        if (!user) return
        let userid = JSON.parse(user);

        if (form.total === 0) return

        // Reset border colors
        cardHolderName.current.style.borderColor = "black"
        cardNumber.current.style.borderColor = "black"
        Expiry_Date_MM.current.style.borderColor = "black"
        Expiry_Date_YY.current.style.borderColor = "black"
        CVV.current.style.borderColor = "black"

        let isCorrect = true

        // ✅ Skip validations if COD
        if (selectedMethod !== "COD") {
            if (form.cardHolderName.length === 0) {
                cardHolderName.current.style.borderColor = "red";
                isCorrect = false;
            }
            if (form.cardNumber.length === 0 || !Number.isInteger(Number(form.cardNumber)) || form.cardNumber.length !== 16) {
                cardNumber.current.style.borderColor = "red";
                isCorrect = false;
            }
            if (form.Expiry_Date_MM.length === 0 || !Number.isInteger(Number(form.Expiry_Date_MM)) || form.Expiry_Date_MM.length !== 2) {
                Expiry_Date_MM.current.style.borderColor = "red";
                isCorrect = false;
            }
            if (form.Expiry_Date_YY.length === 0 || !Number.isInteger(Number(form.Expiry_Date_YY)) || form.Expiry_Date_YY.length !== 4) {
                Expiry_Date_YY.current.style.borderColor = "red";
                isCorrect = false;
            }
            if (form.CVV.length === 0 || !Number.isInteger(Number(form.CVV)) || form.CVV.length !== 3) {
                CVV.current.style.borderColor = "red";
                isCorrect = false;
            }
        }

        if (!isCorrect) return;

        // ✅ Include payment method
        const paymentData = { 
            ...form, 
            products: store.cartList, 
            userid: userid._id, 
            method: selectedMethod 
        };

        fetch("http://localhost:4000/payment", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(paymentData),
            credentials: "include"
        })
        .then(async (res) => {
            const data = await res.json();
            if (data.status) {
                setForm({ cardHolderName: "", cardNumber: "", Expiry_Date_MM: "", Expiry_Date_YY: "", CVV: "", total: 0 })
                store.setCartDetail({"subcost":0,"discount":0,"tax":0,"total":0})
                store.setcartList([])
                alert(`Payment successful via ${selectedMethod}`)
                navigate("/")
            }
            else {
                alert("Error while processing payment")
            }
        })
        .catch((err) => {
            console.log(err)
            alert("Server error")
        })
    }

    return (
        <div className='PaymentPage'>
            <div className="PaymentWindow">
                <img src="./I4.jpg" alt="..." className='PaymetLeftImg' />
                
                <form className="PaymentForm" onSubmit={(e) => handleClick(e)}>
                    <p className='PaymentHeading'>Your details
                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-chevron-right" viewBox="0 0 16 16">
                            <path fillRule="evenodd" d="M4.646 1.646a.5.5 0 0 1 .708 0l6 6a.5.5 0 0 1 0 .708l-6 6a.5.5 0 0 1-.708-.708L10.293 8 4.646 2.354a.5.5 0 0 1 0-.708z" />
                        </svg>
                        <span>Payment</span>
                    </p>

                    {/* ✅ Payment Method Buttons */}
                    <div className="payment-options flex gap-4 mb-4">
                        <button type="button" className={`payment-btn ${selectedMethod === "COD" ? "active" : ""}`} onClick={() => setSelectedMethod("COD")}>COD</button>
                        <button type="button" className={`payment-btn ${selectedMethod === "GPay" ? "active" : ""}`} onClick={() => setSelectedMethod("GPay")}>GPay</button>
                        <button type="button" className={`payment-btn ${selectedMethod === "Paytm" ? "active" : ""}`} onClick={() => setSelectedMethod("Paytm")}>Paytm</button>
                    </div>

                    <p className='PaymetSec'>Fill details (if required)</p>

                    {/* ✅ Hide card inputs if COD */}
                    {selectedMethod !== "COD" && (
                        <>
                            <div className="PaymentItem">
                                <label htmlFor="CardHolderName">Card Holder Name</label>
                                <div className="PAyIconInput">
                                    <input type="text" id='CardHolderName' placeholder='Card Holder Name' ref={cardHolderName} onChange={(e) => setForm({ ...form, cardHolderName: e.target.value })} />
                                </div>
                            </div>
                            <div className="PaymentItem">
                                <label htmlFor="CardNo">Credit card Number</label>
                                <div className="PAyIconInput">
                                    <input type="text" id='CardNo' placeholder='XXXX XXXX XXXX XXXX' ref={cardNumber} onChange={(e) => setForm({ ...form, cardNumber: e.target.value })} />
                                </div>
                            </div>
                            <div className="PaymentItem2">
                                <div className="payItem1">
                                    <label htmlFor="DateMM">Expiry Date</label>
                                    <div className="DateItem">
                                        <input type="text" id='DateMM' placeholder='MM' ref={Expiry_Date_MM} onChange={(e) => setForm({ ...form, Expiry_Date_MM: e.target.value })} />
                                        <input type="text" id='DateYY' placeholder='YYYY' ref={Expiry_Date_YY} onChange={(e) => setForm({ ...form, Expiry_Date_YY: e.target.value })} />
                                    </div>
                                </div>
                                <div className="payItem1">
                                    <label htmlFor="CVV">CVV</label>
                                    <input type="text" id='CVV' placeholder='123' ref={CVV} onChange={(e) => setForm({ ...form, CVV: e.target.value })} />
                                </div>
                            </div>
                        </>
                    )}

                    {/* ✅ Payment Summary */}
                    <div className='PaymentData'>
                        <span>Subtotal</span>
                        <span className='PayB'>₹{store.cartDetail.subcost}</span>
                    </div>
                    <div className='PaymentData'>
                        <span>Discount</span>
                        <span className='PayB'>₹{store.cartDetail.discount}</span>
                    </div>
                    <div className='Payhr'></div>
                    <div className='PaymentData'>
                        <span>Total Amount</span>
                        <span className='PayB'>₹{store.cartDetail.total}</span>
                    </div>

                    {/* Dynamic Pay Button */}
                    <button className="BtnPymentPay" onClick={handleClick}>
                        {selectedMethod === "COD" 
                          ? "Place Order (COD)" 
                          : `Pay with ${selectedMethod}`}
                    </button>
                </form>
            </div>
        </div>
    )
}
