import React from "react";
import { motion } from "framer-motion";

export default function LandingPage() {
  return (
    <>
      <div className="LandingPage min-h-screen bg-gradient-to-br from-orange-50 to-white flex items-center justify-center">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="LandingCon glass-card p-10 rounded-2xl shadow-2xl"
        >
          {/* Heading */}
          <motion.h1
            className="Lheading text-5xl md:text-6xl font-bold text-center mb-8"
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            Enjoy healthy getting fresh grocery
          </motion.h1>

          {/* Quote */}
          <motion.div
            className="LQ flex flex-col md:flex-row justify-between items-center gap-6 mb-10"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1, delay: 0.4 }}
          >
            <div className="LQ1 max-w-lg text-center md:text-left text-gray-600">
              <p className="text-lg italic">
                "Shop fresh, eat well, and save more with our online grocery
                store. Discover the best deals and quality products right at
                your fingertips!"
              </p>
            </div>

            {/* Rating + Profiles */}
            <motion.div
              className="LQ2 flex items-center gap-4"
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ duration: 0.7, delay: 0.6 }}
            >
              <div className="landingFeedBack flex items-center -space-x-3">
                <img src="P1.jpg" alt="..." className="w-10 h-10 rounded-full border-2 border-white" />
                <img src="P2.jpg" alt="..." className="w-10 h-10 rounded-full border-2 border-white" />
                <img src="P3.jpg" alt="..." className="w-10 h-10 rounded-full border-2 border-white" />
              </div>
              <div className="Lrating text-lg font-semibold text-gray-700 flex items-center">
                4.5 ‚≠ê
              </div>
            </motion.div>
          </motion.div>

          {/* Image Grid */}
          <motion.div
            className="LImagesBox grid grid-cols-1 md:grid-cols-3 gap-6"
            initial="hidden"
            animate="visible"
            variants={{
              hidden: { opacity: 0 },
              visible: {
                opacity: 1,
                transition: { staggerChildren: 0.2 },
              },
            }}
          >
            {["I1.jpg", "I2.jpg", "I3.jpg", "I4.jpg", "I5.jpg", "I6.jpg", "I7.jpg"].map(
              (img, index) => (
                <motion.div
                  key={index}
                  className="rounded-xl overflow-hidden shadow-lg hover:scale-105 transition-transform duration-300"
                  initial={{ opacity: 0, y: 40 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.2 }}
                >
                  <img src={`./${img}`} alt="grocery" className="w-full h-60 object-cover" />
                </motion.div>
              )
            )}
          </motion.div>
        </motion.div>
      </div>
    </>
  );
}
