import React, { useContext, useRef, useState } from "react";
import { UserContext } from "../Store";
import { useNavigate } from "react-router-dom";

export default function Payment() {
  const store = useContext(UserContext);
  const [form, setForm] = useState({
    cardHolderName: "",
    cardNumber: "",
    Expiry_Date_MM: "",
    Expiry_Date_YY: "",
    CVV: "",
    total: store.cartDetail.total,
  });

  const [selectedMethod, setSelectedMethod] = useState("COD");
  const cardHolderName = useRef();
  const cardNumber = useRef();
  const Expiry_Date_MM = useRef();
  const Expiry_Date_YY = useRef();
  const CVV = useRef();
  const navigate = useNavigate();

  let handleClick = (e) => {
    e.preventDefault();
    let user = localStorage.getItem("user");
    if (!user) return;
    let userid = JSON.parse(user);

    if (form.total === 0) return;

    // Reset validation styles
    cardHolderName.current.style.borderColor = "black";
    cardNumber.current.style.borderColor = "black";
    Expiry_Date_MM.current.style.borderColor = "black";
    Expiry_Date_YY.current.style.borderColor = "black";
    CVV.current.style.borderColor = "black";

    let isCorrect = true;

    // Skip validations if COD
    if (selectedMethod !== "COD") {
      if (form.cardHolderName.length === 0) {
        cardHolderName.current.style.borderColor = "red";
        isCorrect = false;
      }
      if (
        form.cardNumber.length === 0 ||
        !Number.isInteger(Number(form.cardNumber)) ||
        form.cardNumber.length !== 16
      ) {
        cardNumber.current.style.borderColor = "red";
        isCorrect = false;
      }
      if (
        form.Expiry_Date_MM.length === 0 ||
        !Number.isInteger(Number(form.Expiry_Date_MM)) ||
        form.Expiry_Date_MM.length !== 2
      ) {
        Expiry_Date_MM.current.style.borderColor = "red";
        isCorrect = false;
      }
      if (
        form.Expiry_Date_YY.length === 0 ||
        !Number.isInteger(Number(form.Expiry_Date_YY)) ||
        form.Expiry_Date_YY.length !== 4
      ) {
        Expiry_Date_YY.current.style.borderColor = "red";
        isCorrect = false;
      }
      if (
        form.CVV.length === 0 ||
        !Number.isInteger(Number(form.CVV)) ||
        form.CVV.length !== 3
      ) {
        CVV.current.style.borderColor = "red";
        isCorrect = false;
      }
    }

    if (!isCorrect) return;

    const paymentData = {
      ...form,
      products: store.cartList,
      userid: userid._id,
      method: selectedMethod,
    };

    fetch("http://localhost:4000/payment", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(paymentData),
      credentials: "include",
    })
      .then(async (res) => {
        const data = await res.json();
        if (data.status) {
          setForm({
            cardHolderName: "",
            cardNumber: "",
            Expiry_Date_MM: "",
            Expiry_Date_YY: "",
            CVV: "",
            total: 0,
          });
          store.setCartDetail({ subcost: 0, discount: 0, tax: 0, total: 0 });
          store.setcartList([]);
          alert(`Payment successful via ${selectedMethod}`);
          navigate("/");
        } else {
          alert("Error while processing payment");
        }
      })
      .catch((err) => {
        console.log(err);
        alert("Server error");
      });
  };

  return (
    <div className="flex justify-center py-10 bg-gray-50 min-h-screen">
      <div className="bg-white shadow-2xl rounded-2xl flex overflow-hidden w-full max-w-4xl">
        
        {/* Left Image */}
        <img
          src="./I4.jpg"
          alt="Payment Banner"
          className="w-1/3 object-cover hidden md:block"
        />

        {/* Payment Form */}
        <form
          className="flex-1 p-8 flex flex-col gap-6"
          onSubmit={(e) => handleClick(e)}
        >
          <h2 className="text-2xl font-semibold text-gray-800 flex items-center gap-2">
            Your Details <span className="text-gray-500">›</span> Payment
          </h2>

          {/* Payment Method Buttons */}
          <div className="flex gap-4">
            <button
              type="button"
              className={`flex items-center gap-2 px-4 py-2 rounded-lg border transition ${
                selectedMethod === "COD"
                  ? "bg-gray-800 text-white"
                  : "bg-white text-gray-800"
              }`}
              onClick={() => setSelectedMethod("COD")}
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="20"
                height="20"
                fill="currentColor"
                className="bi bi-cash-stack"
              >
                <path d="M1 3a1 1 0 0 0-1 1v9a1 1 0 0 0 1 1h14a1 1 0 0 0 1-1V4a1 1 0 0 0-1-1H1zm0-1h14a2 2 0 0 1 2 2v9a2 2 0 0 1-2 2H1a2 2 0 0 1-2-2V4a2 2 0 0 1 2-2z" />
                <path d="M13 10.5a2.5 2.5 0 1 1-5 0 2.5 2.5 0 0 1 5 0z" />
              </svg>
              COD
            </button>

            <button
              type="button"
              className={`flex items-center gap-2 px-4 py-2 rounded-lg border transition ${
                selectedMethod === "GPay"
                  ? "bg-blue-600 text-white"
                  : "bg-white text-gray-800"
              }`}
              onClick={() => setSelectedMethod("GPay")}
            >
              <img src="/gpay-logo.png" alt="GPay" className="h-5" />
              GPay
            </button>

            <button
              type="button"
              className={`flex items-center gap-2 px-4 py-2 rounded-lg border transition ${
                selectedMethod === "Paytm"
                  ? "bg-sky-500 text-white"
                  : "bg-white text-gray-800"
              }`}
              onClick={() => setSelectedMethod("Paytm")}
            >
              <img src="/paytm-logo.png" alt="Paytm" className="h-5" />
              Paytm
            </button>
          </div>

          <p className="text-gray-500 text-sm">
            Fill details {selectedMethod === "COD" ? "(not required)" : "below"}
          </p>

          {/* Hide inputs if COD */}
          {selectedMethod !== "COD" && (
            <>
              <input
                type="text"
                placeholder="Card Holder Name"
                ref={cardHolderName}
                className="w-full px-4 py-2 border rounded-lg"
                onChange={(e) =>
                  setForm({ ...form, cardHolderName: e.target.value })
                }
              />
              <input
                type="text"
                placeholder="XXXX XXXX XXXX XXXX"
                ref={cardNumber}
                className="w-full px-4 py-2 border rounded-lg"
                onChange={(e) =>
                  setForm({ ...form, cardNumber: e.target.value })
                }
              />
              <div className="flex gap-4">
                <input
                  type="text"
                  placeholder="MM"
                  ref={Expiry_Date_MM}
                  className="w-1/2 px-4 py-2 border rounded-lg"
                  onChange={(e) =>
                    setForm({ ...form, Expiry_Date_MM: e.target.value })
                  }
                />
                <input
                  type="text"
                  placeholder="YYYY"
                  ref={Expiry_Date_YY}
                  className="w-1/2 px-4 py-2 border rounded-lg"
                  onChange={(e) =>
                    setForm({ ...form, Expiry_Date_YY: e.target.value })
                  }
                />
              </div>
              <input
                type="text"
                placeholder="CVV"
                ref={CVV}
                className="w-full px-4 py-2 border rounded-lg"
                onChange={(e) => setForm({ ...form, CVV: e.target.value })}
              />
            </>
          )}

          {/* Payment Summary */}
          <div className="space-y-2">
            <div className="flex justify-between">
              <span>Subtotal</span>
              <span className="font-medium">₹{store.cartDetail.subcost}</span>
            </div>
            <div className="flex justify-between">
              <span>Discount</span>
              <span className="font-medium">₹{store.cartDetail.discount}</span>
            </div>
            <hr />
            <div className="flex justify-between text-lg font-semibold">
              <span>Total Amount</span>
              <span>₹{store.cartDetail.total}</span>
            </div>
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            className={`mt-4 w-full py-3 rounded-lg font-semibold shadow-md transition ${
              selectedMethod === "COD"
                ? "bg-gray-800 text-white hover:bg-gray-900"
                : selectedMethod === "GPay"
                ? "bg-blue-600 text-white hover:bg-blue-700"
                : "bg-sky-500 text-white hover:bg-sky-600"
            }`}
          >
            {selectedMethod === "COD"
              ? "Place Order (COD)"
              : `Pay with ${selectedMethod}`}
          </button>
        </form>
      </div>
    </div>
  );
}
